// this file is copied into the JSExtension object when producing the XAR
// it should fire everytime a view page is displayed
require(['jquery', 'xwiki-meta'],function(jQuery, Meta) {
    // Velocity
    #set($OO_CONF_REF = $services.model.createDocumentReference('', 'XWikiOnlyOfficeCode', 'ConfigurationClass'))
    #set($OO_CONF_DOC = $xwiki.getDocument($OO_CONF_REF))
    #set($OO_CONF_OBJ = $OO_CONF_DOC.getObject("XWikiOnlyOfficeCode.ConfigurationClass"))
    #set($OO_USE_GLOBAL_CONFIG = $OO_CONF_OBJ.getProperty('useGlobalConfig').getValue())
    #if ("$!OO_USE_GLOBAL_CONFIG" != "0" )
        #set($OO_CONF_REF = $services.model.createDocumentReference($xcontext.getMainWikiName(), 'XWikiOnlyOfficeCode', 'ConfigurationClass'))
        #set($OO_CONF_DOC = $xwiki.getDocument($OO_CONF_REF))
        #set($OO_CONF_OBJ = $OO_CONF_DOC.getObject("XWikiOnlyOfficeCode.ConfigurationClass"))
    #end

    var enableCreation = "$!OO_CONF_OBJ.getProperty('enableCreation').getValue()" == "1";
    // Velocity end

    var MESSAGES = {
        edit: "Edit with OnlyOffce",
        view: "View in OnlyOffice",
        convert: "View/Convert with OnlyOffice"
    };
    var ICONS = {
        edit: "onlyOffice-logo.png",
        view: "onlyOffice-logo-grey.png",
        convert: "onlyOffice-logo-blue.png"
    };

    var rootURL = XWiki.contextPath + "/" + XWiki.servletpath;
    var fullName = XWiki.Document.currentDocumentReference.toString();

    var capitalizeFirstLetter = function (string) {
        return string.charAt(0).toUpperCase() + string.slice(1);
    };

    var getExistingNames = function () {
        return jQuery('#_attachments span.name a').toArray().map(function(a){ return a.innerText });
    };
    // Create untitled documents when no name is given
    var getDefaultName = function (fileType) {
        var untitledIndex = 1;
        var list = getExistingNames();
        var name = ({ '.xlsx': 'Spreadsheet', '.docx': 'Document', '.pptx': 'Presentation' })[fileType] + ' ' + new Date().toString().split(' ').slice(0,4).join(' ');
        if (list.indexOf(name + fileType) === -1) { return name; }
        while (list.indexOf(name + ' - ' + untitledIndex + fileType) !== -1) { untitledIndex++; }
        return name + ' - ' + untitledIndex;
    };

    // New attachment modal
    var displayPopupNew = function (type) {
        XWiki.widgets.NewOOFile = Class.create(XWiki.widgets.ModalPopup, {
            defaultInteractionParameters : {},
            /** Constructor. Registers the key listener that pops up the dialog. */
            initialize : function($super, interactionParameters) {
                this.interactionParameters = Object.extend(Object.clone(this.defaultInteractionParameters), interactionParameters || {});
                // call constructor from ModalPopup with params content, shortcuts, options
                $super(
                    this.createContent(this.interactionParameters),
                    {
                        "show"  : { method : this.showDialog,  keys : [] },
                        "close" : { method : this.closeDialog, keys : ['Esc'] }
                    },
                    {
                        displayCloseButton : true,
                        verticalPosition : "top",
                        backgroundColor : "#FFF",
                        removeOnClose : true
                    }
                );
                this.showDialog();
                this.setClass("my-modal-popup");
            },
            /** Get the content of the modal dialog using ajax */
            createContent : function (data) {
                var content = new Element('div', {'class': 'modal-popup'});
                var newLine = new Element('br');
                var errorP = new Element('p', {'id': 'oONewFileError'});
                var fileType = '.docx';
                var buttonType = 'btn-primary btn-ootext';
                if (type === "spreadsheet") {
                    fileType = '.xlsx';
                    buttonType = 'btn-success btn-oosheet';
                } else if (type === "presentation") {
                    fileType = '.pptx';
                    buttonType = 'btn-warning btn-ooprez';
                }
                var defaultName = getDefaultName(fileType);
                var nameInput = new Element('input', {'size': 30, 'type': 'text', 'id': 'oONewFileName', 'placeholder': defaultName});

                var validButton = new Element('button', {'class': 'btn ' + buttonType});
                validButton.insert("Create " + capitalizeFirstLetter(type));
                jQuery(validButton).on('click', function () {
                    var name = jQuery("#oONewFileName").val().trim();
                    if (name === "") { name = defaultName; }
                    else {
                        var list = getExistingNames();
                        if (list.indexOf(name + fileType) !== -1) {
                            jQuery('#oONewFileError').text(
                                'An attachment with that name ( ' + name + fileType + ' ) ' +
                                'already exists. Please choose another name.'
                            );
                            return;
                        }
                    }
                    var href = rootURL +
                        'view/XWikiOnlyOfficeCode/XooEdit?xpage=plain&' +
                        'document=' + encodeURIComponent(fullName) + '&' +
                        'filename=' + encodeURIComponent(name+fileType) + '&' +
                        'new=1';
                    window.location.href = href;
                });
                content.insert("Name of the " + type);
                content.insert(newLine);
                content.insert(nameInput);
                content.insert(fileType);
                content.insert(errorP);
                content.insert(validButton);
                return content;
            }
        });
        new XWiki.widgets.NewOOFile();
    };

    var decorateWithXooButtons = function () {
        var hasEditRights = XWiki.hasEdit;
        var canDoByExt = { };
        ["docx", "xlsx", "pptx"].forEach(function (x) { canDoByExt[x] = "edit"; });
        [
            "xls", "ods", "csv", "doc", "odt", "ppt", "pps",
            "odp", "ppsx", "rtf", "txt", "mht", "html", "htm"
        ].forEach(function (x) { canDoByExt[x] = "convert"; });
        ["pdf", "djvu", "fb2", "epub", "xps"].forEach(function (x) { canDoByExt[x] = "view"; });

        jQuery("#_attachments span.name").each(function (i, elem) {
            if(jQuery(elem.parentNode).find("div.xwikibuttonlinks > a.oOEdit").length) { return; }
            var fileName = jQuery(elem).find('a').text().trim();
            var fileType = fileName.slice(fileName.lastIndexOf('.') + 1).toLowerCase();
            var whatCanDo = canDoByExt[fileType];
            if (!whatCanDo) { return; }
            if (!hasEditRights) { whatCanDo = "view"; }

            var logoURL = rootURL + 'download/XWikiOnlyOfficeCode/XooEdit/' + ICONS[whatCanDo];
            var label = MESSAGES[whatCanDo];
            jQuery(
                '<div class="btn-group xwikibuttonlinks oobutton" style="padding-left: 5px;">' +
                    '<a class="oOEdit" href= "' + rootURL +
                        'view/XWikiOnlyOfficeCode/XooEdit?xpage=plain&amp;' +
                        'document=' + encodeURIComponent(fullName) + '&amp;' +
                        'filename=' + encodeURIComponent(fileName) + '" ' +
                        'title="' + label + '" class="viewlink btn btn-xs btn-default"' +
                    '>' +
                        '<span class="glyphicon">' +
                            '<img src="' + logoURL + '">' +
                        '</span>' +
                    '</a>' +
                '</div>'
            ).insertBefore(elem);
        });

        if (!enableCreation) { return; }
        if (jQuery('#oOCreateButtons').length) { return; }
        // Add the buttons
        jQuery(
            '<style>' +
                '.btn-ootext {' +
                    'background-image: linear-gradient(#5a7dc9,#496cb8 50%,#385ba7);' +
                    'border-bottom-color: #385ba7;' +
                    'border: 1px solid #5477c3;' +
                    'background-color: #385ba7;' +
                '}' +
                '.btn-ootext:hover {' +
                    'background-image: linear-gradient(#496cb8,#385ba7 50%,#274a96);' +
                    'border-bottom-color: #274a96;' +
                    'border: 1px solid #4366b2;' +
                    'background-color: #274a96;' +
                '}' +
                 '.btn-oosheet {' +
                    'background-image: linear-gradient(#8ca946,#7b9835 50%,#6a8724);' +
                    'border-bottom-color: #6a8724;' +
                    'border: 1px solid #86a340;' +
                    'background-color: #6a8724;' +
                '}' +
                '.btn-oosheet:hover {' +
                    'background-image: linear-gradient(#7b9835,#6a8724 50%,#507613);' +
                    'border-bottom-color: #5076a3;' +
                    'border: 1px solid #759229;' +
                    'background-color: #8076a3;' +
                '}' +
                 '.btn-ooprez {' +
                    'background-image: linear-gradient(#dd682b,#cc571a 50%,#bb4809);' +
                    'border-bottom-color: #bb4809;' +
                    'border: 1px solid #d56223;' +
                    'background-color: #bb4809;' +
                '}' +
                '.btn-ooprez:hover {' +
                    'background-image: linear-gradient(#cc571a,#bb4809 50%,#aa3702);' +
                    'border-bottom-color: #aa3702;' +
                    'border: 1px solid #c45015;' +
                    'background-color: #aa3702;' +
                '}' +
            '</style>' +
            '<div id="oOCreateButtons">' +
                '<input type="button" class="btn btn-primary btn-ootext oONewText" value="New Document" /> ' +
                '<input type="button" class="btn btn-success btn-oosheet oONewSpreadsheet" value="New Spreadsheet" /> ' +
                '<input type="button" class="btn btn-warning btn-ooprez oONewPresentation" value="New Presentation" />' +
            '</div>'
        ).appendTo('#attachmentscontent');

        // Button events
        jQuery('.oONewText').on('click', function () {
            displayPopupNew('document');
        });
        jQuery('.oONewSpreadsheet').on('click', function () {
            displayPopupNew('spreadsheet');
        });
        jQuery('.oONewPresentation').on('click', function () {
            displayPopupNew('presentation');
        });
    };

    jQuery(decorateWithXooButtons);
    jQuery(document).on('xwiki:html5upload:fileFinished', decorateWithXooButtons);
    jQuery(document).on('xwiki:docextra:loaded', decorateWithXooButtons);
});
